editors.value:
mode = None # 'Map' or 'Street View'

map_lat, map_long = 40, -90
map_zoom = 3

def update_map(self):
    self.costume = nb.google_maps.get_map(self.map_lat, self.map_long,
        self.width, self.height, self.map_zoom)

def zoom_by(self, zoom_delta, fov_delta):
    if self.mode == 'Map':
        self.map_zoom = min(25, max(1, self.map_zoom + zoom_delta))
        self.update_map()
@onkey('+')
def zoom_in(self): self.zoom_by(1, -10)
@onkey('-')
def zoom_out(self): self.zoom_by(-1, 10)

def pan_h(self, mult):
    if self.mode == 'Map':
        self.map_long = nb.google_maps.get_longitude_from_x(mult * self.width / 2)
        self.update_map()
@onkey('left arrow', 'a')
def pan_left(self): self.pan_h(-1)
@onkey('right arrow', 'd')
def pan_right(self): self.pan_h(1)

def pan_v(self, mult):
    if self.mode == 'Map':
        self.map_lat = nb.google_maps.get_latitude_from_y(mult * self.height / 2)
        self.update_map()
@onkey('up arrow', 'w')
def pan_up(self): self.pan_v(1)
@onkey('down arrow', 's')
def pan_down(self): self.pan_v(-1)

@onmouse('down')
def clicked(self, x, y):
    if self.mode == 'Map':
        self.mode = 'Chart'
        nb.send_message('update charts', x = x, y = y)
    elif self.mode == 'Chart':
        self.mode = 'Map'
        sprite2.visible = False
        sprite3.visible = False

@onkey('q')
def do_search(self):
    if self.mode != 'Map':
        return

    addr = input('Enter an address:')
    place = nb.geolocation.geolocate(addr)
    self.map_lat = place['latitude']
    self.map_long = place['longitude']
    self.map_zoom = 15
    self.update_map()
