editors.value:
mode = None # 'Map' or 'Street View'
view = None # 'Map' or 'Sat' (for satellite map)

map_lat, map_long = netsblox.get_location()
map_zoom = 15

street_lat, street_long = map_lat, map_long
street_fov = 90
street_heading = 0
street_pitch = 0

def update_map(self):
    if self.view == 'Map':
        self.costume = nb.google_maps.get_map(self.map_lat, self.map_long,
            self.width, self.height, self.map_zoom)
    elif self.view == 'Sat':
        self.costume = nb.google_maps.get_satellite_map(self.map_lat, self.map_long,
            self.width, self.height, self.map_zoom)

def update_street_view(self):
    self.costume = nb.google_street_view.get_view(self.street_lat, self.street_long,
        self.width, self.height, self.street_fov, self.street_heading, self.street_pitch)

def zoom_by(self, zoom_delta, fov_delta):
    if self.mode == 'Map':
        self.map_zoom = min(25, max(1, self.map_zoom + zoom_delta))
        self.update_map()
    elif self.mode == 'Street View':
        self.street_fov = min(120, max(10, self.street_fov + fov_delta))
        self.update_street_view()
@onkey('+')
def zoom_in(self): self.zoom_by(1, -10)
@onkey('-')
def zoom_out(self): self.zoom_by(-1, 10)

@onkey('space')
def exit_street_view(self):
    if self.mode == 'Street View':
        self.mode = 'Map'
        self.update_map()
@onkey('x')
def toggle_map_sat(self):
    if self.mode == 'Map':
        self.view = 'Sat' if self.view == 'Map' else 'Map'
        self.update_map()

def pan_h(self, mult):
    if self.mode == 'Map':
        self.map_long = nb.google_maps.get_longitude_from_x(mult * self.width / 2)
        self.update_map()
    elif self.mode == 'Street View':
        self.street_heading = (self.street_heading + mult * self.street_fov / 9) % 360
        self.update_street_view()
@onkey('left arrow', 'a')
def pan_left(self): self.pan_h(-1)
@onkey('right arrow', 'd')
def pan_right(self): self.pan_h(1)

def pan_v(self, mult):
    if self.mode == 'Map':
        self.map_lat = nb.google_maps.get_latitude_from_y(mult * self.height / 2)
        self.update_map()
    elif self.mode == 'Street View':
        self.street_pitch = min(90, max(-90, self.street_pitch + mult * self.street_fov / 9))
        self.update_street_view()
@onkey('up arrow', 'w')
def pan_up(self): self.pan_v(1)
@onkey('down arrow', 's')
def pan_down(self): self.pan_v(-1)

@onmouse('down')
def clicked(self, x, y):
    if self.mode == 'Map':
        self.mode = 'Street View'
        self.street_lat = nb.google_maps.get_latitude_from_y(y)
        self.street_long = nb.google_maps.get_longitude_from_x(x)
        self.street_fov = 90
        self.street_heading = 0
        self.street_pitch = 0
        self.update_street_view()    
    elif self.mode == 'Street View':
        self.mode = 'Map'
        self.update_map()

@onkey('q')
def do_search(self):
    if self.mode != 'Map':
        return

    addr = input('Enter an address:')
    place = nb.geolocation.geolocate(addr)
    self.map_lat = place['latitude']
    self.map_long = place['longitude']
    self.map_zoom = 15
    self.update_map()
